public class ListPrinterConfiguration {

	private boolean startPrefixSpace;
	private String start;
	private boolean startSuffixSpace;
	private boolean nextPrefixSpace;
	private String separator;
	private boolean nextSuffixSpace;
	private boolean endPrefixSpace;
	private String end;

	public ListPrinterConfiguration(boolean startPrefixSpace, String start, boolean startSuffixSpace, boolean nextPrefixSpace, String next, boolean nextSuffixSpace, boolean endPrefixSpace, String end) {
		this.startPrefixSpace = startPrefixSpace;
		this.start = start;
		this.startSuffixSpace = startSuffixSpace;
		this.nextPrefixSpace = nextPrefixSpace;
		this.separator = next;
		this.nextSuffixSpace = nextSuffixSpace;
		this.endPrefixSpace = endPrefixSpace;
		this.end = end;
	}

	public boolean isStartPrefixSpace() { return startPrefixSpace; }
	public String getStart() { return start; }
	public boolean isStartSuffixSpace() { return startSuffixSpace; }
	public boolean isNextPrefixSpace() { return nextPrefixSpace; }
	public String getSeparator() { return separator; }
	public boolean isNextSuffixSpace() { return nextSuffixSpace; }
	public boolean isEndPrefixSpace() { return endPrefixSpace; }
	public String getEnd() { return end; }

}
