package spoon.reflect.visitor;

public class ListPrinterConfig {

	public final boolean startPrefixSpace;
	public final String start;
	public final boolean startSuffixSpace;
	public final boolean nextPrefixSpace;
	public final String next;
	public final boolean nextSuffixspace;
	public final boolean endPrefixSpace;
	public final String end;

	public ListPrinterConfig(boolean startPrefixSpace, String start, boolean startSuffixSpace, boolean nextPrefixSpace, String next, boolean nextSuffixSpace, boolean endPrefixSpace, String end) {
		this.startPrefixSpace = startPrefixSpace;
		this.start = start;
		this.startSuffixSpace = startSuffixSpace;
		this.nextPrefixSpace = nextPrefixSpace;
		this.next = next;
		this.nextSuffixspace = nextSuffixSpace;
		this.endPrefixSpace = endPrefixSpace;
		this.end = end;
	}
}
